## ==========================================================================
## Retrieve unique identifier of a flowFrame
## (normally generated by the cytometer and normally unique)
## "$FIL" optional field replace by filename if missing
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("identifier", signature="flowFrame",
          definition=function(object){
            oid <- object@description[["GUID"]]
            if(is.null(oid) || is.na(oid))
               oid <- as.vector(object@description[["$FIL"]])
            if(is.null(oid) || is.na(oid))
                oid <- as.vector(object@description[["FILENAME"]])
            if(is.null(oid) || is.na(oid))
                "anonymous"
            else
              as.vector(oid)
        })


## ==========================================================================
## accessor and replace methods for slot exprs
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("exprs", signature="flowFrame",
          definition=function(object)
            object@exprs
          )

setReplaceMethod("exprs", signature=c("flowFrame", "matrix"),
                 definition=function(object, value) {
                   object@exprs <- value
                   return(object)
                 })


## ==========================================================================
## accessor method for slot parameters
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("parameters", signature="flowFrame",
          definition=function(object)
            object@parameters
          )


## ==========================================================================
## accessor methods for items in the description slot
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("keyword",signature("flowFrame","character"), function(object,keyword) {
	structure(object@description[keyword],names=keyword)
})
setMethod("keyword",signature("flowFrame","function"), function(object,keyword) {
	keyword(object,object@description)
})
setMethod("keyword",signature("flowFrame","list"), function(object,keyword) {
	sapply(keyword,function(k) {
		if(is.character(k))
			object@description[k]
		else if(is.function(k))
			k(object,object@description,k)
		else NA
	})
})
setMethod("keyword",signature("flowFrame","missing"), function(object) {
    object@description
})

## ==========================================================================
## accessor methods for slot description
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("description",signature("flowFrame"),function(object) object@description)


## ==========================================================================
## accessor and replace methods for colnames of exprs slot
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("colnames", signature="flowFrame",
          definition=function(x, do.NULL="missing", prefix="missing")
          colnames(exprs(x))
          )

setReplaceMethod("colnames", signature=c("flowFrame", "ANY"),
                 definition=function(x, value) {
                   colnames(x@exprs) <- value
                   return(x)
                 })


## ==========================================================================
## accessor method for feature names
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("featureNames", signature="flowFrame",
          definition=function(object)
          object@parameters$desc
          )


## ==========================================================================
## accessor method for names
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("names", signature="flowFrame",
          definition=function(x) {
            cn <- colnames(x)
            fn <- featureNames(x)
            if(length(fn) == length(cn)) {
              cn <- paste("<", cn, ">", sep="")
              for(i in seq(along=fn)) {
                if(!is.na(fn[i]) && fn[i]!="")
                  cn[i] <- paste(cn[i],fn[i])
              }
            }
            cn
          })


## ==========================================================================
## a simple plot method without strange plot parameter and friends. It does
## the most intuitive thing: take a flowFrame and plot the first two columns
## of the data. If you want to plot other columns, subset the frame before
## plotting. For the more complicated stuff there may well be more elaborate
## methods but I advice to separate functionality. Let R do the method
## dispatching instead of putting everything into one "do-it-all" method.
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("plot", signature(x="flowFrame", y="missing"),
          definition=function(x, ...){
            values=exprs(x)
            geneplotter:::smoothScatter(values[,1:2], ...)
          })

setMethod("plot",signature(x="flowFrame",y="character"),function(x,y,...) {
	l = length(y)
	if(l==1)
		hist(exprs(x)[,y],...)
	else if(l==2) {
		geneplotter:::smoothScatter(exprs(x)[,y],...)
	}
})



## ==========================================================================
## the $-operator (subsetting with '$')
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
"$.flowFrame" <- function(x, val)
    x[,val]


## ==========================================================================
## subsetting methods
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("[", signature="flowFrame",
          definition=function(x, i, j, ..., drop=FALSE) {
            exprs(x) <-  switch(1+missing(i)+2*missing(j),
                                { exprs(x)[i, j, ..., drop=drop] },
                                { exprs(x)[ , j, ..., drop=drop] },
                                { exprs(x)[i,  , ..., drop=drop] },
                                { exprs(x)[ ,  , ..., drop=drop] } )
            if(!missing(j)) 
              x@parameters = x@parameters[j,]
            x
          })
setMethod("[", signature=signature("flowFrame","filterResult"),
          definition=function(x,i,j,...,drop=FALSE) {
            if(missing(j))
              x[x %in% i,,...,drop=drop]
            else
              x[x %in% i,j,...,drop=drop]
          })
setMethod("[", signature=signature("flowFrame","filter"),
          definition=function(x,i,j,...,drop=FALSE) {
			result = filter(x,i)
            if(missing(j))
              x[result,,...,drop=drop]
            else
              x[result,j,...,drop=drop]
          })


