          )


## ==========================================================================
## show method for flowFrame
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("show",signature=signature("flowFrame"),
          definition=function(object) {
            dm <- dim(exprs(object))
            msg <- paste("flowFrame object with ", dm[1], " cells and ", 
                         dm[2], " observables:\n", paste(names(object), 
                         collapse = " "), "\nslot 'description' has ",
                         length(description(object)), " elements\n", sep = "")
            cat(msg)
            return(msg)
          })


## ==========================================================================
## Subset methods for flowFrame: Why is this Subset with capital 's' ???
## Why do we need the 'select' parameter? Wouldn't this be equivalent:
## Subset(x[,c(1,3)], subset)
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -  
setMethod("Subset", signature("flowFrame","filter"),
          definition=function(x,subset,select,...){
            if(!missing(select))
              Subset(x,x %in% subset,select,...)
            else
              Subset(x,x %in% subset,...)
          })
setMethod("Subset",signature("flowFrame","logical"),
          definition=function(x,subset,select,...) {
            if(!missing(select))
              x[subset & !is.na(subset),select]
            else x[subset,]
          })


## ==========================================================================
## split methods for flowFrame
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("split", signature("flowFrame","filter"),
          definition=function(x,f,drop=FALSE,...)
          split(x,filter(x,f),drop,...)
          )
#We actually filter on filterResults and multipleFilterResults
setMethod("split", signature("flowFrame","logicalFilterResult"),
          definition=function(x,f,drop=FALSE,population=NULL,prefix=NULL,...) {
	        if(is.null(population)) population=f@filterId
			if(!is.null(prefix)) paste(prefix,population,sep="")
            structure(list(x[f@subSet,],x[!f@subSet,]),
                      names=c(paste(population,"+",sep=""),
                        paste(population,"-",sep="")))
          })
setMethod("split", signature("flowFrame","multipleFilterResult"),
          function(x,f,drop=FALSE,prefix=NULL,...) {
            nn = if(is.null(prefix)) names(f) else paste(prefix,names(f),sep="")
            structure(lapply(seq(along=f),function(i) x[f[[i]],]),names=nn)
})


## ==========================================================================
## summary method for flowFrame
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("summary", signature("flowFrame"), 
    function(object, ...) 
        apply(exprs(object), 2, summary)
 )


## ==========================================================================
## wrappers for apply (over exprs slot)
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("each_row",signature("flowFrame"),function(x,FUN,...) {
	apply(exprs(x),1,FUN,...)
})
setMethod("each_col",signature("flowFrame"),function(x,FUN,...) {
	apply(exprs(x),2,FUN,...)
})


## ===========================================================================
## spillover method
## ---------------------------------------------------------------------------
setMethod("spillover","flowFrame",function(x) {
	## Flow frames have a SPILL keyword that is often
	## used to store the spillover matrix. Attempt to
	## extract it.
	present <- unlist(keyword(x,"spillover"))
        if(is.null(present)) stop("No spillover matrix stored in that flowFrame")
        else present
})


## ===========================================================================
## compensate method
## ---------------------------------------------------------------------------
setMethod("compensate",signature("flowFrame","matrix"),function(x,spillover) {
	## Make sure we're normalized to [0,1] and then invert
	cols = colnames(spillover)
	e    = exprs(x)
	e[,cols] = e[,cols]%*%solve(spillover/max(spillover))
	exprs(x) = e
	x
})


## ==========================================================================
## transform method
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("transform",
          signature=signature(`_data`="flowFrame"),
          definition=function(`_data`, ...) {
              e <- substitute(list(...))
              x = `_data`
              transformed <- as.matrix(transform(as.data.frame(exprs(x)),...))
              ##Add any new parameter values
              if(ncol(transformed) > ncol(x@exprs)) {
              	cnames = c(colnames(x),colnames(transformed)[-c(1:ncol(x@exprs))])
              }
              else {
              	cnames = colnames(x)
              }
              ##param.names <- colnames(transformed)
              ##newParams <- is.na(match(param.names,`_data`@parameters$name))
              ##params      = parameters(`_data`)$name
              ##if(any(newParams)) {
              ##    params <- cbind(params,
              ##                    data.frame(name=param.names))
                  ##params = cbind(params,
                  ##    data.frame(name=param.names[newParams]))
              ##}
              #colnames(transformed) <- parameters(`_data`)$name
              colnames(transformed) = cnames
              new("flowFrame",
                  exprs=transformed, 
                  parameters=parameters(x),#[,params$name],
                  description=description(x))
          })


## ==========================================================================
## filter method
## - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
setMethod("filter", signature(x="flowFrame", filter="filter"),
          function(x, filter) {
            result <- as(x %in% filter, "filterResult")
            result@filterId <- filter@filterId
            result@parameters <- filter@parameters
            filterDetails(result, result@filterId) <- filter
            result@frameId <- identifier(x)
            result
          })
